    // Break the array into smaller arrays
    const n = array.length;
    let temp: Array<number> = [];
    for (let i = n - 1; i >= 1; i -= 1) {
        const j = i;
        const k = i - 1;
        while (j >= 1 && array[j] > array[k]) {
            const temp = array[j];
            array[j] = array[k];
            array[k] = temp;
            j -= 1;
            k -= 1;
