    $input[] = $item;
  }

  echo "Output:<br>";
  print_r($output);
  echo "</br>\n<br>";

  foreach ($input as $item) {
    echo "Input:<br>";
    echo "Name: ".$item['name']."<br>";
    echo "Age: ".$item['age']."<br>";
    echo "Job: ".$item['job']."<br>";
  }
?>

