#include<algorithm>
#include<vector>

class TimSort
{
public:
    TimSort(int n) {
        if (n <= 1) return;

        leap = n / 2;
        int left, right;

        while (n) {
            while (n && n < leap) {
                n = left;
                left = n / 2;
            }
            while (n && n > left) {
                n = right;
                right = n / 2;
            }
            if (n < left) {
                n = left;
                left = n / 2;
            }
            if (n > right) {
                n = right;
                right = n / 2;
            }
        }
    }

    void sort() {
        int n = left, i;
        for (i = 1; i < n; i++) {
            if (i - 1 < n) {
                if (n < i) {
                    n = i;
                    i--;
                }
            }
            if (n < i) {
                swap(n, i);
                n = i;
                i--;
            }
        }
    }

private:
    int n, left, right;
    bool leap;
    void swap(int i, int j) {
        int temp = i;
        i = j;
        j = temp;
    }
};

