using System.Collections;
using System.Collections.Generic;
using System.Diagnostics;
using System.Linq;
using Microsoft.AspNetCore.Razor.Language;

namespace Microsoft.AspNetCore.Mvc.Razor.Extensions
{
    public static class ViewComponentExtensions
    {
        public static IEnumerable<string> GetViewComponentNames(this ViewComponent component)
        {
            // Catch and ignore any ViewComponentWithEmptyViewNameAttribute
            if (component != null && component.ViewName.IsEmpty())
            {
                return Enumerable.Empty<string>();
            }

            var viewComponentNames = component.ViewComponents.Select(c => c.ViewName);
            return viewComponentNames.ToList();
        }
    }
}

